CREATE TABLE IF NOT EXISTS `uctoo_yuyue` (
`id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
`yTime`  int(10) NOT NULL  COMMENT '预约时间',
`market`  varchar(255) NOT NULL  COMMENT '门店',
`phone`  varchar(32) NOT NULL  COMMENT '手机',
`item`  varchar(255) NOT NULL  COMMENT '预约项目',
`token`  varchar(255) NOT NULL  COMMENT 'Token',
`cTime`  int(10) UNSIGNED NOT NULL  COMMENT '创建时间',
`number`  int(10) NOT NULL  COMMENT '预约人数',
`mTime`  int(10) NOT NULL  COMMENT '修改时间',
`name`  varchar(255) NOT NULL  COMMENT '联系人',
`status`  int(10) NOT NULL  DEFAULT 1 COMMENT '状态',
`remark`  varchar(2000) NOT NULL  COMMENT '备注',
`memo`  varchar(2000) NOT NULL  COMMENT '备忘',
`openid`  varchar(255) NOT NULL  COMMENT '粉丝openid',
PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci CHECKSUM=0 ROW_FORMAT=DYNAMIC DELAY_KEY_WRITE=0;
UPDATE `uctoo_attribute` SET model_id= (SELECT MAX(id) FROM `uctoo_model`) WHERE model_id=0;
